import * as React from 'react';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Toolbar from '@mui/material/Toolbar';
import dashboard from '../../assets/dashboard.png';
import courses from '../../assets/courses.png';
import trainee from '../../assets/trainees.png';
import Logo from '../Logo/Logo';

const drawerWidth = 140;

function Sidebar(props) {
  const { window } = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const getIndexImage = (index) => {
    if (index === 0) {
      return dashboard;
    } else if (index === 1) {
      return courses;
    } else if (index === 2) {
      return trainee;
    }
    // Return a default image if index is not 1, 2, or 3
    return null;
  };

  const drawer = (
    <div>
      <Toolbar />
      <List>
        {['Dashboard', 'Courses', 'Trainees'].map((text, index) => (
          <ListItem className='sidebar-list' key={text} disablePadding>
            <ListItemButton>
              <div className='sidebar-item'>
                <ListItemIcon className='sidebar-item-icon'>
                    <img src={getIndexImage(index)}></img>
                </ListItemIcon>
                <ListItemText className='sidebar-text' primary={text} />
              </div>
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </div>
  );

  const container = window !== undefined ? () => window().document.body : undefined;

  return (
    <Box  sx={{ display: 'flex' }}>
      <Logo />
      <CssBaseline />

        <Box 

            component="nav"
            sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 }}}
            aria-label="mailbox folders"
        >
            <Drawer
                container={container}
                variant="temporary"
                open={mobileOpen}
                onClose={handleDrawerToggle}
                ModalProps={{
                    keepMounted: true, // Better open performance on mobile.
                }}
                sx={{
                    display: { xs: 'block', sm: 'none' },
                    '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
                }}
            >
            {drawer}
            </Drawer>
            <Drawer
            className='sidebar-element'
                variant="permanent"
                sx={{
                    display: { xs: 'none', sm: 'block' },
                    '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
                }}
                open
            >
            {drawer}
            </Drawer>
        </Box>
        <Divider/>
    </Box>
  );
}

Sidebar.propTypes = {
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

export default Sidebar;
